pages/amp/product:
---
product:
  reviews:
    limit: 99
  videos: true
  related_products:
    limit: {{theme_settings.related-products}}
---

{{> core/amp/pages/product
  header_background=theme_settings.color_background
  header_text=theme_settings.color_text
  body_background=theme_settings.color_background
  color_headings=theme_settings.color_headings
  color_text=theme_settings.color_text
  footer_background=theme_settings.color_highlight
  footer_text=theme_settings.color_text
  color_accent=theme_settings.color_primary
  color_borders=theme_settings.color_borders
}}

{{#partial 'amp-cart-icon'}}
  <div class="amp-mini-cart">
    {{> components/header/mini-cart}}
  </div>
{{/partial}}

{{#partial 'amp-custom-header-styles'}}
  {{> components/amp/styles/mini-cart}}
{{/partial}}

{{#partial 'amp-custom-product-styles'}}
  {{> components/amp/styles/product}}
{{/partial}}

{{#partial 'amp-additional-info'}}
  {{> components/product/tabs/additional-info}}
{{/partial}}

{{#partial 'amp-footer'}}
  {{> core/amp/common/footer
    theme_credits=theme_settings.theme_credits
    theme_name="Brixton"
    store_copyright=theme_settings.store_copyright
  }}
{{/partial}}

{{#partial 'amp-social'}}
  {{> components/common/link-lists
    type='social'
  }}
{{/partial}}

{{#partial 'amp-custom-footer-styles'}}
  {{> components/amp/styles/social-links}}
{{/partial}}
{{> layout/amp}}

core/amp/pages/product:
{{#partial "amp-style"}}
  {{> core/amp/products/styles
    header_background=../header_background
    header_text=../header_text
    body_background=../body_background
    color_headings=../color_headings
    color_text=../color_text
    footer_background=../footer_background
    footer_text=../footer_text
    color_accent=../color_accent
    color_borders=../color_borders
  }}
{{/partial}}

{{#partial "amp-scripts"}}
  {{#any product.images product.videos}}
    <script async custom-element="amp-carousel" src="https://cdn.ampproject.org/v0/amp-carousel-0.1.js"></script>
  {{/any}}
  {{#if product.options}}
    <script async custom-element="amp-iframe" src="https://cdn.ampproject.org/v0/amp-iframe-0.1.js"></script>
  {{/if}}
  {{#if product.videos}}
    <script async custom-element="amp-youtube" src="https://cdn.ampproject.org/v0/amp-youtube-0.1.js"></script>
  {{/if}}
  {{#if settings.add_this.buttons}}
    <script async custom-element="amp-social-share" src="https://cdn.ampproject.org/v0/amp-social-share-0.1.js"></script>
  {{/if}}
{{/partial}}


{{#partial "page"}}
  {{> core/amp/common/header}}
  {{> core/amp/products/product-view}}

  {{#block "amp-footer"}} {{/block}}

  {{#if settings.amp_analytics_id}}
    <amp-analytics type="googleanalytics">
      <script type="application/json">
      {
        "vars": {
          "account": "{{settings.amp_analytics_id}}"
        },
        "extraUrlParams": {
          "cd1": "{{page_type}}",
          "cd2": "{{category.id}}",
          "cd3": "{{category.url}}",
          "cd4": "{{category.name}}"
        },
        "triggers": {
          "trackPageview": {
            "on": "visible",
            "request": "pageview"
          },
          "trackClickOnProductCard" : {
            "on": "click",
            "selector": ".amp-grid-item-link, .amp-grid-item-name a",
            "request": "event",
            "vars": {
              "eventCategory": "Category",
              "eventAction": "Product Card"
            },
            "extraUrlParams": {
              "cd6": "${productLink}",
              "cd7": "${productId}",
              "cd8": "${productName}"
            }
          }
        }
      }
      </script>
    </amp-analytics>
  {{/if}}
  {{> core/structured-data/product-reviews}}
{{/partial}}

components/amp/styles/mini-cart:
.mini-cart-toggle {
  display: flex;
  align-items: center;
  color: {{color_text}};
}

.mini-cart-toggle:hover {
  opacity: 1;
}

.mini-cart-icon-wrap {
  height: 18px;
}

.mini-cart-icon-wrap.cart-empty .mini-cart-icon-full {
  display: none;
}

.mini-cart-icon-wrap.cart-full .mini-cart-icon-empty {
  display: none;
}

.mini-cart-icon-wrap.cart-full .mini-cart-icon-full {
  display: inline-block;
}

.mini-cart-count {
  display: none;
  margin-left: 5px;
  font-size: 16px;
  line-height: 18px;
}

.mini-cart-count.show {
  display: inline-block;
}

components/common/link-lists:
{{!
  Link lists

  @param type  options
         'pages', 'social' and 'brands' to determine content of the link list
}}

<div class="link-list-wrapper">
  <ul class="link-list">
    {{#if type '==' 'pages'}}
      <li class="link-list-title">
        {{lang 'link-list.pages'}}
      </li>
      {{#each pages}}
        <li class="link-list-item">
          <a href="{{url}}">
            {{name}}
          </a>
        </li>
      {{/each}}
      {{#if settings.gift_certificates_enabled}}
        <li class="link-list-item">
          <a href="{{urls.gift_certificate.purchase}}">
            {{lang 'link-list.gift_certificates'}}
          </a>
        </li>
      {{/if}}
      {{#if theme_settings.show_sitemap}}
        <li class="link-list-item sitemap">
          <a href="{{urls.sitemap}}">{{lang 'sitemap.title'}}</a>
        </li>
      {{/if}}
    {{/if}}

    {{#if type '==' 'brands'}}
      <li class="link-list-title">
        {{lang 'link-list.brands'}}
      </li>
      {{#each shop_by_brand}}
        <li class="link-list-item">
          <a href="{{url}}">{{name}}</a>
        </li>
      {{/each}}
      <li class="link-list-item">
        <a href="{{urls.brands}}" class="link-list-view-all">
          {{lang 'link-list.view_brands'}}
        </a>
      </li>
    {{/if}}

    {{#if type '==' 'social'}}
      <li class="link-list-title">
        {{lang 'link-list.social'}}
      </li>
      {{#each social_media}}
        <li class="link-list-item">
          <a href="{{url}}" class="{{display_name}}" target="_blank">
            {{name}}
          </a>
        </li>
      {{/each}}
    {{/if}}
  </ul>
</div>

components/amp/styles/social-links:
.amp-footer-credits .link-list {
  list-style: none;
  margin-left: 0;
  margin-bottom: 20px;
}

.amp-footer-credits .link-list-title {
  font-weight: 700;
  margin-bottom: 10px;
}

.amp-footer-credits .link-list-item {
  display: inline-block;
  padding: 0 5px;
}

core/structured-data/product-reviews:
{{!-- Product Reviews: include in product.html page template --}}

{{#if settings.show_product_reviews}}
  {{#each product.reviews.list}}
    <script type="application/ld+json">
      {
        "@context": "http://schema.org/",
        "@type": "Review",
        "itemReviewed": {
          "@type": "Product",
          "image": "{{getImage ../product.main_image}}",
          "name": "{{../product.title}}"
        },
        "reviewRating": {
          "@type": "Rating",
          "ratingValue": "{{rating}}"
        },
        "name": "{{title}}",
        "author": {
          "@type": "Person",
          "name": "{{name}}"
        },
        "reviewBody": "{{text}}",
        "publisher": {
          "@type": "Organization",
          "name": "{{../settings.store_name}}"
        }
      }
    </script>
  {{/each}}
{{/if}}

core/amp/css/base:
*,
