ul {
  margin: 0;
  padding: 0;
}

li {
  padding: 0;
  margin: 0;
}

a {
  color: {{color_accent}};
  line-height: inherit;
  text-decoration: none;
}

a:hover,
a:focus {
  opacity: 0.7;
}

