  font-size: 28px;
}

h2 {
  font-size: 25px
}

h3 {
  font-size: 22px;
}

h4 {
  font-size: 20px;
}

h5 {
  font-size: 15px;
}

h6 {
  font-size: 13px;
  text-transform: uppercase;
}

hr {
  border: solid {{color_borders}};
  border-width: 1px 0 0;
  clear: both;
  height: 0;
  margin: 2rem 0 1.92857rem;
}

ul,
ol,
dl {
  list-style-position: outside;
  margin-bottom: 1.5rem;
}

ul ul,
ul ol,
ol ul,
ol ol {
  margin-left: 1.2rem;
  margin-bottom: 0;
}

ul {
  margin-left: 1.15rem;
}

ol {
  margin-left: 1.15rem;
}

dl dt {
  font-weight: 700;
  margin-bottom: .35714rem;
}

dl dd {
  margin-bottom: 1.5rem;
}

abbr,
acronym {
  border-bottom: 1px dotted {{color_borders}};
  color: {{color_text}};
