  margin-top: 1rem;
}

.amp-navigation-link {
  display: block;
  font-size: 15px;
  font-weight: 500;
  color: {{color_text}};
  padding: .78571rem 0;
  text-decoration: none;
}

.amp-navigation-tier-item.has-children > .amp-navigation-link {
  color: {{color_text}};
}

.amp-navigation-link[aria-expanded="true"] .arrow-down {
  transform: rotate(-90deg);
